import React, { useState } from 'react';
import "./Login.css";
import profil from "../assets/profil.png";
import mail from "../assets/mail.png";
import pwd from "../assets/pwd.png";


const Login = () => { // Change 'login' to 'Login'
  const [action, setAction] = useState("Login");

  return (
    <div className="container">
      <div className="header">
        <div className="underline" onClick={() => setAction(action === "Log in" ? "Sign up" : "Log in")}></div>
      </div>

      <div className="inputs">
        {action === "Login"?<div></div>:<div className="input">
          <img src={profil} alt='' className="img"/>
          <input type="text" placeholder="Username" />
        </div>}
        

        <div className="input">
          <img src={mail} alt='' className="img"/>
          <input type="text" placeholder="Email" />
        </div>

        <div className="input">
          <img src={pwd} alt='' className="img"/>
          <input type="password" placeholder="Password" />
        </div>
      </div>

      {action === "Sign up"?<div></div>:<div className="forgot-password">
        Forgot password<span>Click here!!</span>
      </div>}
      
      <div className="submit-container">
        <div className={action === "Login" ? "submit gray" : "submit"}onClick={()=>{setAction("Sign up")}}>
          Sign up
        </div>
        <div className={action === "Sign up" ? "submit gray" : "submit"}onClick={()=>{setAction("Login")}}>
          Login
        </div>
      </div>
    </div>
  );
};

export default Login; // Change 'login' to 'Login'
